<?phpclass db_connect {        private $_config;        private $_conn;        public function __construct(){            $this->_config = parse_ini_file('../config/config.ini',true);        }        public  function connect(){                        $result =array(                'status' =>'',                'conn'=>'',                'info'=>'',            );            try{                $this->_conn = new mysqli(                    $this->_config['database']['host'],                    $this->_config['database']['user'],                    $this->_config['database']['password'],                    $this->_config['database']['dbname']                );                $result['status']='Y';                $result['conn'] = $this->_conn;                $result['info']='Connect Success!';            }catch(mysqli_sql_exception $e){                /*                    After -> change the new exception system                */                $result['status']='N';                $result['info']=mysqli_connect_error();            }            unset($this->_config);            return $result;        }        public function disconnect(){            $result =array(                'status' =>'',                'conn'=>'',                'info'=>'',            );                        try{                mysqli_close($this->_conn);                $result['status']='Y';                $result['conn'] = $this->_conn;                $result['info']='Close Success!';            }catch (mysqli_sql_exception $e){                 $result['status']='N';                $result['info']=mysqli_connect_error();            }              return $result;        }}